mapper.select:
SELECT os.id as id,od.id as ordersDetailsId, os.order_number, os.user_id, os.username, os.merchant_id as marketProducesId, os.merchant_name, os.address_book_id, os.consignee_name, os.address_book_name, os.phone_number, os.status, os.complete_time, os.cancel_time, os.remark, os.deleted, os.create_time, os.update_time, od.id, produce_name, image, produce_category, produce_weight, unit_price, amount FROM produce.orders AS os JOIN produce.orders_detail AS od ON os.id = od.orders_id
and os.user_id = #{id}
and od.produce_name LIKE CONCAT('%', #{producesName}, '%')
and os.merchant_name LIKE CONCAT('%', #{merchantName}, '%')
and od.status = #{status}
and od.create_time BETWEEN #{startCreateTime} AND #{endCreateTime}
and os.deleted = 1
ORDER BY os.id DESC LIMIT #{pageNo}, #{pageSize};

SELECT count(*)
FROM produce.orders AS os
JOIN produce.orders_detail AS od ON os.id = od.orders_id


        and os.user_id = #{id}


        and od.produce_name LIKE CONCAT('%', #{producesName}, '%')


        and os.merchant_name LIKE CONCAT('%', #{merchantName}, '%')


        and od.status = #{status}


        and od.create_time BETWEEN #{startCreateTime} AND #{endCreateTime}


        and os.deleted = 1
